INSERT INTO `tb_branchschool` VALUES ('213', '1094', '44', '启智阁滨江区分校', '启智阁Kidsglobal', '滨江区滨盛路彩虹城33幢1楼商铺启智阁', '120.16517157142', '30.195089262191', '400-0168-055', '113高峰大站车; 113路; 172路; 197路; 352路; b支3路; b支6路均可到彩虹城小区站下车', '', 'root2.png', '     启智阁（Kidsglobal）是来自新加坡的儿童教育机构；专为中国0-12岁的儿童提供优质的双语学习环境。启智阁以英语学习为主，通过启智阁特有的“学、做、应用”，既“LEARN-DO-APPLY”授学模式，让小朋友们在欢乐中探索、发现与实践，学习并掌握一定的技能、知识与价值观，既SKILLS,KNOWLEDGE,VALUES。', '1', '2014-03-04 13:43:17', '2014-03-04 13:43:17');
