INSERT INTO `tb_curriculum` VALUES ('111', '1', '43', '2', null, '12', '33', '0', '179', '高一精培', '0', '0', 'root2.png', '以提高词汇和语法知识的灵活运用能力为目；以阅读理解为介入点，让学生\r\n在掌握大量的词汇和语法的同时提高学生的综合理解和立阅读能力；\r\n', '新三册和高一教材的完美结合，同时满足学生阅读量词汇量及综合应试能力。\r\n', '高一在读学生及具备新概念二册学完基础学生', '0', '掌握新概念三册1-15课及高一教材的重点篇目词汇及语法应用。提升学生的词汇语法水平及综合运用能力。', '29', '新概念三册+高一教材', '2013-09-15', '2014-01-05', '周日上午8：00-10:00', '32小时', '1800', '80', '高二精培', '0571-88920623', '2013-09-24 00:50:43', '2014-02-01 11:25:37');
