INSERT INTO `tb_curriculum` VALUES ('382', '2', '52', '11', null, '4', '76', '0', '0', '三年级作文积累与阅读（下）', '0', '1', 'root2.png', '', '◆从基础阅读中学习写作方法，在写作训练中应用基础知识；\r\n◆课程中融入部分学校课本知识，各个年级接轨学校课程程度；\r\n◆课程内容由启扬教育专职语文教师紧扣教学大纲设计编写。\r\n', '◆在校同年级学生', '1', '◆掌握基础，通过大量阅读训练提升阅读解题技巧，提升写作能力。', '22', '', '', '', '◆周日下午1:00-3:00', '32小时', '1880元', '0元', '', '028－86990827', '2014-01-13 13:30:45', '2014-01-13 13:30:45');
