INSERT INTO `tb_curriculum` VALUES ('764', '1222', '39', '237', null, '1', '582', '0', '0', 'FD', '0', '0', 'root2.png', '适合4岁以上的小朋友，全外教上课。', '全外教上课，课堂气氛活跃。运用自然拼读法培养孩子的英语语感和学习兴趣。', '4岁以上', '1', '培养孩子的英语发音，让孩子在全英语的环境下提高口语和听力，提高语言运用能力。', '20@33', '', '', '', '每次课1.5小时', '105小时', '14700，有折扣', '100', '', '87756368', '2014-03-17 14:10:16', '2014-03-17 14:31:19');
