INSERT INTO `tb_information` VALUES ('26', '软件介绍', '<p><span style=\"font-family:宋体;\"><span style=\"color:#009900;\">&nbsp; &nbsp; &nbsp;校掌是为学生及学生家长提供各方面教育培训信息、校区选择、课程查找、优惠信息的公共平台，同时兼顾培训机构、学生及学生家长的交流平台，实现了学生在线学习，家校实时联系等功能。让培训学校能够更加轻松的将自己的校区和开设的课程、开班及优惠等各种信息及时传达到目标客户群体；让学生及家长能够足不出户轻松选择到适合自己的课程；让培训学校里的老师与学生及其家长能够更密切、便捷的沟通联系，并让学生在学习中找到快乐。校掌</span></span><span lang=\"EN-US\" style=\"color:#009900;\">APP</span><span style=\"font-family:宋体;color:#009900;\">将为培训机构、学生及学生家长提供全方位的一站式服务。</span></p>\r\n<p><span style=\"font-family:宋体;color:#009900;\"><br />\r\n</span></p>\r\n<p>校掌APP功能介绍</p>\r\n<p class=\"MsoNormal\"></p>\r\n<p class=\"MsoNormal\" align=\"left\"></p>\r\n<p class=\"MsoNormal\" align=\"left\"></p>\r\n<p><span style=\"font-family:宋体;\">1、家校沟通：通过聊天功能，家长、学生与老师能够及时方便地传递信息。</span></p>\r\n<p><span style=\"font-family:宋体;\">2、选课报班：通过对课程的详细介绍，方便您在移动端选课、查询课程，给家长学生提供快捷平台。</span></p>\r\n<p class=\"MsoNormal\" align=\"left\"></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">3</span><span style=\"font-family:宋体;\">、找适合：随时随地查询最适合您的校区课程、地址、电话。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">4</span><span style=\"font-family:宋体;\">、校区地图：轻松查找校区位置。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">5</span><span style=\"font-family:宋体;\">、信息公告：可以快捷方便的看到培训学校发布的最新消息，动态。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p></p>', 'root2.png', '3', '2013-09-24 01:09:27');
