INSERT INTO `tb_school` VALUES ('799', '40', '杭州哇啦哇啦英语（杭州华来教育）', '', 'www.walawalaenglish.com', '浙江省杭州市下城区中山北路607号现代城建大厦205室', '120.17182343389', '30.284933712938', '0571-86602960、4009900056', '外教口语幼儿班（K班）、外教口语少儿班（K班）、青少儿英语（UP班）', '150', '0571-86602960', '', '0571-86602960', '汪天来', 'hz@walawalaenglish.com', '13805795903', '2645036820', '任献华、汪天来', '1', '330103000163318', '吴国君', null, '', '', null, '哇啦哇啦英语（Walawala　English）致力于青少儿英语教育事业，是为数不多的结合国际学校的英语教学方式立足母语式教学的专业教育集团，自2006年在上海成立以来，计划覆盖全国大范围一线二线城市，并由总部统一协助提供国际标准化的管理支持。　\r\nWalaWalaEnglish教材为哇啦哇啦国际团队和美国西北大学的Northwestern　University语言学院的教授共同研发，并与美国出版界的巨头麦格劳一希尔McGraw-Hills合作引进了旗下教育与专业出版集团（Educational　＆　Professional　Group）的原版同步教材。哇啦哇啦英语教学体系揉合了Phonics、歌曲、韵律试、阅读、写作、自然、数学、自然科学、常识等学科，能够从多个角度启发孩子的英语学习兴趣，加上轻松、自由、创新，以兴趣为导向的先进教育机制，使得孩子的学习效果事半功倍，成为中国孩子学习英语的一站式加油站，让中国的孩子学英语像母语一样简单！\r\n', null, '1', '2013-12-26 10:40:13', '2013-12-27 13:40:41');
