INSERT INTO `tb_school` VALUES ('1072', '453', '郑州新拓普外语学校', '', '', '河南省郑州市金水区南阳路303号', '113.65735319758', '34.777017413299', '0371-63925252', '', '50', '', '', '', '', '', '', '', '', '1', '无登记', '', 'root2.png', '', '', 'root2.png', '新拓普 --快乐英语大本营。 新拓普学校成立于2000年，由郑州大学西亚斯国际学院的教育精英和众多外教及郑州南阳路、八中、优胜路中、小学退休高级教师组成的专家教育团队，共同精心打造的、独具特色的新型外语培训品牌。', null, '1', '2014-02-21 14:08:29', '2014-02-21 14:08:29');
