INSERT INTO `tb_school` VALUES ('1336', '811', '黄冈李阳疯狂英语培训中心', '', '', '湖北省黄冈市黄州区东门路1号摩尔城02B栋4单元107', '114.88657352692', '30.456317719051', '0713-83900567', null, '50', '', 'hgcrazyenglish@163.com', '', '', '', '', '', '', '1', '无登记', '', 'root2.png', '', '', 'root2.png', '黄冈李阳疯狂英语培训中心是在李阳老师亲临指导下于二零零九年五月成立的一所专门针对英语的培训学校，是目前李阳疯狂英语在湖北黄冈地区唯一的授权培训机构。我校秉承李阳老师“让三亿中国人讲一口流利的英语”的理念，致力于传播积聚李阳老师20年心血的精品学习方法，促使每一位学员在最短的时间内，用最有效的方法，掌握最地道的英语！为中国的英语教育事业贡献力量！\r\n我校常年开设暑假、寒假封闭、半封闭集训营、一对一VIP班和各种日常班，招收少儿、小学、中学、大学、成人学员。', null, '1', '2014-03-20 12:34:59', '2014-03-20 12:34:59');
